mapper.select:
select u.user_id, u.dept_id, u.login_name, u.user_name, u.email, u.phonenumber, u.password, u.sex, u.avatar, u.salt, u.status, u.user_career, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, d.dept_name from sys_user u
left join sys_dept d on u.dept_id = d.dept_id
where u.del_flag = '0'

	AND d.dept_code in

		#{dept}



	AND u.user_career = #{careerCode}

SELECT u.user_id, u.dept_id, u.login_name, u.user_name, u.email, u.phonenumber, u. PASSWORD, u.sex, u.avatar, u.salt, u. STATUS, u.user_career, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, u.change_pwd_date, u.position_code, d.dept_name FROM sys_user u LEFT JOIN sys_dept d ON u.dept_id = d.dept_id left join sys_user_role ur on ur.user_id = u.user_id left join sys_role r on r.role_id = ur.role_id WHERE u.del_flag = '0'
AND d.dept_code in
#{dept}
AND r.role_key = #{roleCode}

select u.user_id, u.dept_id, u.login_name, u.user_name, u.email, u.phonenumber, u.password, u.sex, u.avatar, u.salt, u.status, u.card_num, u.user_career, u.del_flag, u.login_ip, u.login_date, u.create_by,u.change_pwd_date,u.position_code, u.create_time, u.remark, d.dept_name, wmsys.wm_concat(r.role_name) AS role_name, (SELECT COUNT(0) FROM sastind_asset_account t WHERE t.liable_user_id = u.user_id) as account_num from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0'
AND u.login_name like concat('%', concat(#{sysUser.loginName}, '%'))
AND u.user_name like concat('%', concat(#{sysUser.userName}, '%'))
AND u.status = #{sysUser.status}
AND u.sex = #{sysUser.sex}
AND r.role_id = #{sysUser.role}
AND u.phonenumber like concat('%', concat(#{sysUser.phonenumber}, '%'))
AND date_format(u.create_time,'%y%m%d') >= date_format(#{sysUser.params.beginTime},'%y%m%d')
AND date_format(u.create_time,'%y%m%d') <= date_format(#{sysUser.params.endTime},'%y%m%d')
AND u.dept_id IN
#{deptId}
${sysUser.params.dataScope} GROUP BY u.user_id, u.dept_id, u.login_name, u.user_name, u.email, u.phonenumber, u.password, u.sex, u.avatar, u.salt, u.status,u.card_num, u.user_career, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, d.dept_name, u.change_pwd_date,u.position_code
